function likes(names) {
  let people = names
  let output = "";
  for (let i = 0; i < people.length; i++) {
    if (people.length < 2) {
      output += people[i];
    }else{
      output += people[i] + " and ";
    }
  }
  console.log(output)
}

likes(['james','ali'])
